import React, { useState } from 'react';
import './Post.css'
import Avatar from '@material-ui/core/Avatar';

export default function Post({username, caption, imageUrl}) {

  const post__image = {
    width: "100%",
    objectFit: "contain",
    borderTop: "1px solid lightgray",
    borderButtom: "1px solid lightgray",
  }
  const post__text = {
    fontWeight: "normal",
    padding: "20px",
  }
  const [comments, setComments] = useState('')

  return (      
    <div className="post">
      <div className="post__header">
        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" className="post__avatar" />
        <h3>{username}</h3>
      </div>

        <img className="post__image" style={post__image} src={imageUrl} alt="" />
        <h4 className="post__text" style={post__text}><b>{username}</b>: {caption}</h4>
    </div>
  );
}
