import React, { useState, useEffect } from 'react';
import './Post.css'
import Avatar from '@material-ui/core/Avatar';
import { db } from './firebase';

export default function Post({postId, username, caption, imageUrl}) {
  const [comment, setComment] = useState([])

  useEffect(() => {
    let unsubscribe;
    if (postId) {
      unsubscribe = db
      .collection("posts")
      .doc(postId)
      .collection("comments")
      .onSnapshot((snapshot) => {
        setComment(snapshot.docs.map((doc) => doc.data()))
      })
  }
  return () => {
    unsubscribe()
  }
}, [postId])


  const post__image = {
    width: "100%",
    objectFit: "contain",
    borderTop: "1px solid lightgray",
    borderButtom: "1px solid lightgray",
  }
  const post__text = {
    fontWeight: "normal",
    padding: "20px",
  }

  return (      
    <div className="post">
      <div className="post__header">
        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" className="post__avatar" />
        <h3>{username}</h3>
      </div>

        <img className="post__image" style={post__image} src={imageUrl} alt="" />
        <h4 className="post__text" style={post__text}><b>{username}</b>: {caption}</h4>
        <form>
          <input 
          className="post__input"
          type="text" 
          placeholder="Add a comment"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          />
        </form>
    </div>
  );
}
