import React, { useState, useEffect } from 'react';
import './Post.css'
import Avatar from '@material-ui/core/Avatar';
import { db } from './firebase';
import firebase from 'firebase';

export default function Post({postId, username, caption, imageUrl, user}) {
  const [comments, setComments] = useState([])
  const [comment, setComment] = useState('')

  useEffect(() => {
    let unsubscribe;
    if (postId) {
      unsubscribe = db
      .collection("posts")
      .doc(postId)
      .collection("comments")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshot) => {
        setComments(snapshot.docs.map((doc) => doc.data()))
      })
  }
  return () => {
    unsubscribe()
  }
}, [postId])

const postComment = (event) => {
  event.preventDefault()
  db.collection("posts").doc(postId).collection("comments").add({
    text: comment,
    username: user.displayName,
    timestamp: firebase.firestore.FieldValue.serverTimestamp()
  })
  setComment("")
}
  const post__image = {
    width: "100%",
    objectFit: "contain",
    borderTop: "1px solid lightgray",
    borderButtom: "1px solid lightgray",
  }
  const post__text = {
    fontWeight: "normal",
    padding: "20px",
  }

  return (      
    <div className="post">
      <div className="post__header">
        <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" className="post__avatar" />
        <h3>{username}</h3>
      </div>

        <img className="post__image" style={post__image} src={imageUrl} alt="" />
        <h4 className="post__text" style={post__text}><strong>{username}</strong>: {caption}</h4>

          <div className="post__comments">
            {comments.map((comment) => (
              <p>
                <strong>{comment.username}</strong> {comment.text}
              </p>
            ))}
          </div>

          {user && (
          <form className='post__commentBox'>
              <input 
              className="post__input"
              type="text" 
              placeholder="Add a comment ..."
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              />
              <button disabled={!comment}
              className="post__button"
              type="submit"
              onClick={postComment}>
                Post
              </button>
        </form>
          )}

    </div>
  );
}
